var params = {"peer_id": {0}, "offset": {1}, "count": 200};
var i = 0;
var items = [];
while (i<25) {
    var result = API.messages.getHistory(params);
    if (result.items.length == 0) {
        i = 25;
    } else {
        items = items + result.items;
        params.offset = params.offset + 200;
        i = i + 1;
    }
};
